<script>
import TweenMax from "gsap";

import github from "~/components/icons/github.vue";
import codepen from "~/components/icons/codepen.vue";
import linkedin from "~/components/icons/linkedin.vue";
import arrowright from "~/components/icons/arrows/arrowright.vue";

export default {
  components: {
    github,
    codepen,
    linkedin,
    arrowright
  },
  transition: {
    duration: 5000,
    css: false,
    beforeEnter(el) {
      TweenMax.set("#home_wrap h1, #home_wrap h2, #home_wrap a", {
        x: -100,
        autoAlpha: 0
      });
    },
    enter(el, done) {
      var tl = new TimelineLite({
        onComplete: done,
        ease: Back.easeInOut.config(1.7)
      });
      tl.to("#home_wrap h1", 0.5, { x: 0, autoAlpha: 1 })
        .to("#home_wrap h2", 0.5, { x: 0, autoAlpha: 1 }, "-=0.25")
        .to("#home_wrap a", 0.5, { x: 0, autoAlpha: 1 }, "-=0.35");
    },
    afterEnter(el) {},
    enterCancelled(el) {},
    beforeLeave(el) {},
    leave(el, done) {
      var tl = new TimelineLite({
        onComplete: done,
        ease: Back.easeInOut.config(1.7)
      });
      tl.to("#home_wrap a", 0.5, { x: -100, autoAlpha: 0 })
        .to("#home_wrap h2", 0.5, { x: -100, autoAlpha: 0 }, "-=0.25")
        .to("#home_wrap h1", 0.5, { x: -100, autoAlpha: 0 }, "-=0.35");
    },
    // appear() {
    //   console.log('whhaatt');

    // },
    afterLeave(el) {},
    leaveCancelled(el) {}
  },
  computed: {
    pageData: function() {
      return this.$store.state.pages[0];
    }
  },
  mounted() {
    if (process.browser) {
      // console.log(this.$store.state.categories);
      // console.log(this.$store.state.projects);

      document.querySelector("body").classList.remove("body-light");

      var w = window.innerWidth;
      var h = window.innerHeight;
    }
  },
  head() {
    return {
      htmlAttrs: {
        class: "home-page"
      }
    };
  }
};
</script>

